import numpy as np

def encode_bit(bit, basis):
    if bit == 0:
        if basis == 0:
            return np.array([1, 0])
        else:
            return np.array([1, 1]) / np.sqrt(2)
    else:
        if basis == 0:
            return np.array([0, 1])
        else:
            return np.array([1, -1]) / np.sqrt(2)

def measure_bit(state, basis):
    if basis == 0:
        if np.allclose(state, np.array([1, 0])):
            return 0
        else:
            return 1
    else:
        if np.allclose(state, np.array([1, 1]) / np.sqrt(2)):
            return 0
        else:
            return 1

