    const pattern = /\[\[\s*(?:DRAW|IMAGE)\s*\|(.+?)\]\]/gi;
    const matches = text.match(pattern);
    if (!matches) {
        return [];
    }

    return matches.map((match) => match.replace(pattern, '$1').trim());
